import React, { useState } from "react";
import axios from "axios";

const RequestDemo = () => {
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });

  const handleSubmit = async () => {
    const scriptURL =
      "https://script.google.com/macros/s/AKfycbxs4Iq2rFQEBCswCh-Og86pVDd0Y-xG0r7lOL2xHIrlZ7FIlMF1rlRZItG4jgESHGI9/exec";

    try {
      await axios.post(scriptURL, formData);
      alert("‚úÖ Demo request submitted!");
      setFormData({ name: "", email: "", message: "" });
      setOpen(false);
    } catch (error) {
      console.error("Error submitting form", error);
      alert("‚ùå Failed to submit. Please try again.");
    }
  };

  return (
    <>
      <button
        className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition"
        onClick={() => setOpen(true)}
      >
        Request a Demo
      </button>

      {open && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg w-full max-w-md shadow-lg relative">
            <button
              className="absolute top-2 right-3 text-gray-500 hover:text-black"
              onClick={() => setOpen(false)}
            >
              ‚úï
            </button>
            <h2 className="text-xl font-bold mb-4">Request a Demo</h2>
            <input
              className="w-full p-2 mb-3 border rounded"
              type="text"
              placeholder="Your Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            />
            <input
              className="w-full p-2 mb-3 border rounded"
              type="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            />
            <textarea
              className="w-full p-2 mb-3 border rounded"
              placeholder="Message (Optional)"
              value={formData.message}
              onChange={(e) => setFormData({ ...formData, message: e.target.value })}
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              onClick={handleSubmit}
            >
              Submit
            </button>
          </div>
        </div>
      )}
    </>
  );
};

const AuditricLandingPage = () => {
  return (
    <div className="font-sans">
      {/* Hero */}
      <section className="text-center py-20 bg-gradient-to-b from-white to-gray-50">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 text-gray-800">
          Automate Compliance with <span className="text-blue-600">Auditric</span>
        </h1>
        <p className="text-gray-600 text-lg max-w-xl mx-auto mb-6 px-4">
          Simplify audit workflows, collect data, and stay compliant across all your frameworks ‚Äì all in one place.
        </p>
        <RequestDemo />
      </section>

      {/* Features */}
      <section className="bg-white py-16">
        <div className="grid md:grid-cols-3 gap-6 px-6 max-w-6xl mx-auto">
          {[
            {
              icon: "üìä",
              title: "Real-time Dashboards",
              desc: "Visualize audit progress, SLA tracking, and ticket resolution timelines in one place.",
            },
            {
              icon: "‚öôÔ∏è",
              title: "Automated Ticketing",
              desc: "Trigger workflows and assign tasks automatically with our smart engine.",
            },
            {
              icon: "üõ°Ô∏è",
              title: "Built-in Governance",
              desc: "Ensure policy and compliance alignment with E15 and ISO frameworks.",
            },
          ].map((item) => (
            <div key={item.title} className="bg-gray-50 shadow p-6 rounded-lg text-center">
              <div className="text-3xl mb-3">{item.icon}</div>
              <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
              <p className="text-gray-600">{item.desc}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-100 py-6 text-center text-sm text-gray-500">
        &copy; {new Date().getFullYear()} Auditric. All rights reserved.
      </footer>
    </div>
  );
};

export default AuditricLandingPage;
